package basicprogram;

public class BitwiseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=20,num2=10;
		System.out.println("Result for bitwise AND :"+(num1&num2));
		System.out.println("Result for bitwise OR :"+(num1|num2));
		System.out.println("Result for bitwise XOR :"+(num1^num2));
		System.out.println("Result for bitwise Right Shift :"+(num1>>2));
		System.out.println("Result for bitwise Left Shift :"+(num1<<2));

	}

}
