package basicprogram;

public class UnaryExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;
		System.out.println("Initial Value :"+num1);
		System.out.println("Prefix increment :"+(++num1));
		System.out.println("Prefix increment :"+(num1++));
		System.out.println("Current Value :"+num1);
		System.out.println("Prefix decrement :"+(--num1));
		System.out.println("Postfix decrement :"+(num1--));
		System.out.println("Current Value :"+num1);

	}

}
