package pattern;

public class SpaceDcr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,j,s;
		
		for(i=0;i<5;i++) {
			
        
			for(s=1;s<=i;s++) {
				
				System.out.print(" ");}
            for(j=4;j>=i;j--) {
				
				System.out.print("*");
			}
			
			
			System.out.println();

	}

}
	
}
